import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth <= 768);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    if (isDropdownOpen) {
      dropdownRef.current.focus();
    }
  }, [isDropdownOpen]);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const renderLargeScreenHeader = () => (
    <>
      {/* Logo */}
      <Link to="/" className="logo">
        <i className="fa fa-store"></i> TPOP
      </Link>

      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for products..."
          className="search-box"
        />
        <button className="search-button">
          <i className="fa fa-search"></i>
        </button>
      </div>

      {/* Account & Cart */}
      <div className="profile-container">
        <i className="fa fa-user profile-icon"></i>
        <a href="/login">Login</a> | <a href="/signup">Signup</a>
        <div className="cart-container">
          <i className="fa fa-shopping-cart"></i>
          <span>Rs0.00 (0 Items)</span>
        </div>
      </div>

      {/* Triple Line Menu Button for Small Screens */}
      <button className="menu-button" onClick={toggleDropdown}>
        <i className="fa fa-bars"></i>
      </button>
    </>
  );

  const renderSmallScreenHeader = () => (
    <>
      {/* Logo */}
      <Link to="/" className="logo">
        <i className="fa fa-store"></i> TPOP
      </Link>

      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for products..."
          className="search-box"
        />
        <button className="search-button">
          <i className="fa fa-search"></i>
        </button>
      </div>

      {/* Triple Line Menu Button for Small Screens */}
      <button className="menu-button" onClick={toggleDropdown}>
        <i className="fa fa-bars"></i>
      </button>
    </>
  );

  return (
    <header className="header">
      <div className="header-container">
        {isSmallScreen ? renderSmallScreenHeader() : renderLargeScreenHeader()}
      </div>

      {/* Dropdown Menu */}
      {isDropdownOpen && (
        <div
          className="dropdown"
          ref={dropdownRef}
          tabIndex="-1"
          onKeyDown={handleKeyDown}
        >
          <ul className="dropdown-list" id="dropdown-list">
            <li><a href="/login">Login</a></li>
            <li><a href="/signup">Signup</a></li>
            <li><a href="/cart">Cart</a></li>
            <li><a href="/wishlist">Wishlist</a></li>
            <li><a href="/account">Account</a></li>
            <li><a href="/">Home Appliances</a></li>
            <li><a href="/">Fashion & Clothing</a></li>
            <li><a href="/">Kitchen Appliances</a></li>
            <li><a href="/">Cosmetic & Beauty</a></li>
            <li><a href="/">Furniture & Sofas</a></li>
            <li><a href="/">Women's Fashion</a></li>
            <li><a href="/">Men's Fashion</a></li>
            <li><a href="/">Computer & Laptop</a></li>
            <li><a href="/">Flowers & Gift Items</a></li>
            <li><a href="/">Mobiles & Accessories</a></li>
          </ul>
        </div>
      )}
    </header>
  );
}

export default Header;
