import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import MainContent from './MainContent';
import Footer from './Footer';
import './EcommerceLayout.css';

function EcommerceLayout() {
  const [categories] = useState([
    "Home Appliances",
    "Fashion & Clothing",
    "Kitchen Appliances",
    "Cosmetic & Beauty",
    "Furniture & Sofas",
    "Women's Fashion",
    "Men's Fashion",
    "Computer & Laptop",
    "Flowers & Gift Items",
    "Mobiles & Accessories",
  ]);

  const [featuredProducts] = useState([
    // Add your featured products here
  ]);

  const [bestSellingProducts] = useState([
    // Add your best selling products here
  ]);

  const [topSellingProducts] = useState([
    // Add your top selling products here
  ]);

  return (
    <div className="ecommerce-layout">
      <Header />
      <div className="content">
        <Sidebar categories={categories} />
        <MainContent categories={categories} />
      </div>
      <div className="promo-image-container">
        <img src="path/to/your/promo-image.jpg" alt="Promo" className="promo-image" />
      </div>
      <div className="product-section">
        <h2>Featured Products</h2>
        <div className="product-list">
          {featuredProducts.slice(0, 5).map((product, index) => (
            <div key={index} className="product-item">
              {/* Render product details here */}
            </div>
          ))}
        </div>
        <button className="show-more-button">Show More</button>
      </div>
      <div className="product-section">
        <h2>Best Selling</h2>
        <div className="product-list">
          {bestSellingProducts.slice(0, 5).map((product, index) => (
            <div key={index} className="product-item">
              {/* Render product details here */}
            </div>
          ))}
        </div>
        <button className="show-more-button">Show More</button>
      </div>
      <div className="product-section">
        <h2>Top Selling</h2>
        <div className="product-list">
          {topSellingProducts.slice(0, 5).map((product, index) => (
            <div key={index} className="product-item">
              {/* Render product details here */}
            </div>
          ))}
        </div>
        <button className="show-more-button">Show More</button>
      </div>
      <Footer />
    </div>
  );
}

export default EcommerceLayout;
