import React, { useState, useRef, useEffect } from 'react';
import './Header.css';

function Header() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    if (isDropdownOpen) {
      dropdownRef.current.focus();
    }
  }, [isDropdownOpen]);

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo */}
        <div className="logo">
          <i className="fa fa-store"></i> istore
        </div>

        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            placeholder="I am shopping for..."
            className="search-box"
          />
          <button className="search-button">
            <i className="fa fa-search"></i>
          </button>
        </div>

        {/* Account & Cart */}
        <div className="profile-container">
          <i className="fa fa-user profile-icon"></i>
          <a href="/login">Login</a> | <a href="/signup">Signup</a>
          <div className="cart-container">
            <i className="fa fa-shopping-cart"></i>
            <span>Rs0.00 (0 Items)</span>
          </div>
        </div>
      </div>

      {/* Navigation Bar */}
      <nav className="nav-bar">
        <button
          className="categories-button"
          onClick={toggleDropdown}
          aria-expanded={isDropdownOpen}
          aria-controls="dropdown-list"
        >
          Categories ▼
        </button>
        {isDropdownOpen && (
          <div
            className="dropdown"
            ref={dropdownRef}
            tabIndex="-1"
            onKeyDown={handleKeyDown}
          >
            <ul className="dropdown-list" id="dropdown-list">
              <li tabIndex="0">Home Appliances</li>
              <li tabIndex="0">Fashion & Clothing</li>
              <li tabIndex="0">Kitchen Appliances</li>
              <li tabIndex="0">Cosmetic & Beauty</li>
              <li tabIndex="0">Furniture & Sofas</li>
              <li tabIndex="0">Women's Fashion</li>
              <li tabIndex="0">Men's Fashion</li>
              <li tabIndex="0">Computer & Laptop</li>
              <li tabIndex="0">Flowers & Gift Items</li>
              <li tabIndex="0">Mobiles & Accessories</li>
            </ul>
          </div>
        )}
      </nav>
    </header>
  );
}

export default Header;
