import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import MainContent from './MainContent';
import Footer from './Footer';
import ProductSection from './ProductSection';
import './EcommerceLayout.css';

function EcommerceLayout() {
  const [categories] = useState([
    "Home Appliances",
    "Fashion & Clothing",
    "Kitchen Appliances",
    "Cosmetic & Beauty",
    "Furniture & Sofas",
    "Women's Fashion",
    "Men's Fashion",
    "Computer & Laptop",
    "Flowers & Gift Items",
    "Mobiles & Accessories",
  ]);

  const featuredProducts = [
    { id: 1, name: "Product 1", price: 100 },
    { id: 2, name: "Product 2", price: 200 },
    { id: 3, name: "Product 3", price: 300 },
    { id: 4, name: "Product 4", price: 400 },
    { id: 5, name: "Product 5", price: 500 },
    { id: 6, name: "Product 6", price: 600 },
    { id: 7, name: "Product 7", price: 700 },
    { id: 8, name: "Product 8", price: 800 },
  ];

  const bestSellingProducts = [
    { id: 1, name: "Product A", price: 150 },
    { id: 2, name: "Product B", price: 250 },
    { id: 3, name: "Product C", price: 350 },
    { id: 4, name: "Product D", price: 450 },
    { id: 5, name: "Product E", price: 550 },
    { id: 6, name: "Product F", price: 650 },
    { id: 7, name: "Product G", price: 750 },
    { id: 8, name: "Product H", price: 850 },
  ];

  const topSellingProducts = [
    { id: 1, name: "Product X", price: 120 },
    { id: 2, name: "Product Y", price: 220 },
    { id: 3, name: "Product Z", price: 320 },
    { id: 4, name: "Product W", price: 420 },
    { id: 5, name: "Product V", price: 520 },
    { id: 6, name: "Product U", price: 620 },
    { id: 7, name: "Product T", price: 720 },
    { id: 8, name: "Product S", price: 820 },
  ];

  return (
    <div className="ecommerce-layout">
      <Header />
      <div className="content">
        <Sidebar categories={categories} />
        <MainContent categories={categories} />
      </div>
      <div className="promo-image-container">
        <img src="path/to/your/promo-image.jpg" alt="Promo" className="promo-image" />
      </div>
      <ProductSection title="Featured Products" products={featuredProducts} />
      <ProductSection title="Best Selling" products={bestSellingProducts} />
      <ProductSection title="Top Selling" products={topSellingProducts} />
      <Footer />
    </div>
  );
}

export default EcommerceLayout;
