import React, { useState } from 'react';
import './Header.css';

function Header() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo */}
        <div className="logo">
          <i className="fa fa-store"></i> istore
        </div>

        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            placeholder="I am shopping for..."
            className="search-box"
          />
          <button className="search-button">
            <i className="fa fa-search"></i>
          </button>
        </div>

        {/* Account & Cart */}
        <div className="profile-container">
          <i className="fa fa-user profile-icon"></i>
          <a href="/login">Login</a> | <a href="/signup">Signup</a>
          <div className="cart-container">
            <i className="fa fa-shopping-cart"></i>
            <span>Rs0.00 (0 Items)</span>
          </div>
        </div>
      </div>

      {/* Navigation Bar */}
      <nav className="nav-bar">
        <button className="categories-button" onClick={toggleDropdown}>
          Categories â–¼
        </button>
        {isDropdownOpen && (
          <div className="dropdown">
            <ul className="dropdown-list">
              <li>Home Appliances</li>
              <li>Fashion & Clothing</li>
              <li>Kitchen Appliances</li>
              <li>Cosmetic & Beauty</li>
              <li>Furniture & Sofas</li>
              <li>Women's Fashion</li>
              <li>Men's Fashion</li>
              <li>Computer & Laptop</li>
              <li>Flowers & Gift Items</li>
              <li>Mobiles & Accessories</li>
            </ul>
          </div>
        )}
      </nav>
    </header>
  );
}

export default Header;
