import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const sliderRef = useRef(null);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth <= 768);

  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      setIsSliderOpen(false);
    }
  };

  useEffect(() => {
    if (isSliderOpen) {
      sliderRef.current.focus();
    }
  }, [isSliderOpen]);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const renderLargeScreenHeader = () => (
    <>
      {/* Logo */}
      <Link to="/" className="logo">
        <i className="fa fa-store"></i> TPOP
      </Link>

      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for products..."
          className="search-box"
        />
        <button className="search-button">
          <i className="fa fa-search"></i>
        </button>
      </div>

      {/* Account & Cart */}
      <div className="profile-container">
        <i className="fa fa-user profile-icon"></i>
        <a href="/login">Login</a> | <a href="/signup">Signup</a>
        <div className="cart-container">
          <i className="fa fa-shopping-cart"></i>
          <span>Rs0.00 (0 Items)</span>
        </div>
      </div>
    </>
  );

  return (
    <header className="header">
      <div className="header-container">
        {/* Show the triple-line menu button for small screens */}
        {isSmallScreen && (
          <button className="menu-button" onClick={toggleSlider}>
            <i className="fa fa-bars"></i>
          </button>
        )}

        {/* Render the full header for large screens */}
        {!isSmallScreen && renderLargeScreenHeader()}
      </div>

      {/* Slider Menu for Small Screens */}
      {isSmallScreen && isSliderOpen && (
        <div
          className="slider-menu"
          ref={sliderRef}
          tabIndex="-1"
          onKeyDown={handleKeyDown}
        >
          {/* Close Button */}
          <button className="close-button" onClick={toggleSlider}>
            <i className="fa fa-times"></i>
          </button>

          {/* Search Bar in Slider Menu */}
          <div className="search-container">
            <input
              type="text"
              placeholder="Search for products..."
              className="search-box"
            />
            <button className="search-button">
              <i className="fa fa-search"></i>
            </button>
          </div>

          {/* Navigation Links */}
          <ul className="slider-menu-list">
            <li><a href="/login">Login</a></li>
            <li><a href="/signup">Signup</a></li>
            <li><a href="/cart">Cart</a></li>
            <li><a href="/wishlist">Wishlist</a></li>
            <li><a href="/account">Account</a></li>
            <li><a href="/">Home Appliances</a></li>
            <li><a href="/">Fashion & Clothing</a></li>
            <li><a href="/">Kitchen Appliances</a></li>
            <li><a href="/">Cosmetic & Beauty</a></li>
            <li><a href="/">Furniture & Sofas</a></li>
            <li><a href="/">Women's Fashion</a></li>
            <li><a href="/">Men's Fashion</a></li>
            <li><a href="/">Computer & Laptop</a></li>
            <li><a href="/">Flowers & Gift Items</a></li>
            <li><a href="/">Mobiles & Accessories</a></li>
          </ul>
        </div>
      )}
    </header>
  );
}

export default Header;
