import React, { useState } from 'react';
import { useLocation } from "react-router-dom";
import { Star, ShoppingCart, MessageSquare, Heart } from "lucide-react";

const topSelling = [
  { id: 1, name: "Ethnic Motif Woven Design Saree", price: 1500 },
  { id: 2, name: "Abstract Printed Round Neck Kurta", price: 1000 },
  { id: 3, name: "Women Floral Printed Kurta", price: 10 },
];

export default function ProductPage() {
  const location = useLocation();
  const { product } = location.state || {};
  const [selectedImage, setSelectedImage] = useState(product?.images[0] || '');
  const [quantity, setQuantity] = useState(1);

  if (!product) {
    return <div className="p-6 bg-white max-w-6xl mx-auto">Product not found.</div>;
  }

  return (
    <div className="p-6 bg-white max-w-6xl mx-auto">
      {/* Main Layout */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        {/* Product Images */}
        <div className="md:col-span-1">
          <img src={selectedImage} alt="Product" className="w-full h-72 object-cover border rounded-lg" />
          <div className="flex gap-2 mt-3">
            {product.images.map((img, index) => (
              <img
                key={index}
                src={img}
                alt="Thumbnail"
                className={`w-16 h-16 border p-1 cursor-pointer rounded ${
                  selectedImage === img ? "border-red-500" : "border-gray-300"
                }`}
                onClick={() => setSelectedImage(img)}
              />
            ))}
          </div>
        </div>

        {/* Product Details */}
        <div className="md:col-span-2 space-y-3">
          <h1 className="text-2xl font-bold">{product.name}</h1>
          <p className="text-gray-500">Brand: <span className="text-blue-500">{product.brand}</span></p>

          {/* Rating, Wishlist, and Inquiry */}
          <div className="flex items-center gap-4">
            <div className="flex items-center">
              <Star className="text-gray-300" /> <span className="text-gray-500 ml-1">(0 reviews)</span>
            </div>
            <button className="text-red-500 flex items-center"><Heart className="mr-1" /> Add to Wishlist</button>
            <button className="text-blue-500">Product Inquiry</button>
          </div>

          {/* Price */}
          <p className="text-red-600 text-2xl font-bold">Rs {product.price.toFixed(2)}</p>

          {/* Quantity Selector */}
          <div className="flex items-center gap-3">
            <button 
              className="px-4 py-1 border rounded"
              onClick={() => setQuantity(q => (q > 1 ? q - 1 : 1))}
            >-</button>
            <span className="px-4">{quantity}</span>
            <button 
              className="px-4 py-1 border rounded"
              onClick={() => setQuantity(q => (q < product.stock ? q + 1 : q))}
            >+</button>
          </div>

          {/* Total Price */}
          <p className="text-lg font-semibold">Total: Rs {(product.price * quantity).toFixed(2)}</p>

          {/* Action Buttons */}
          <div className="flex gap-3">
            <button className="bg-green-500 text-white px-6 py-2 rounded flex items-center">
              <ShoppingCart className="mr-2" /> Add to Cart
            </button>
            <button className="bg-red-500 text-white px-6 py-2 rounded">Buy Now</button>
          </div>

          <button className="bg-gray-100 px-6 py-2 rounded flex items-center">
            <MessageSquare className="mr-2" /> Message Seller
          </button>
        </div>

        {/* Sidebar: Top Selling Products */}
        <div className="md:col-span-1">
          <h2 className="text-lg font-semibold mb-2">Top Selling Products</h2>
          {topSelling.map((item) => (
            <div key={item.id} className="flex items-center gap-3 border-b py-2">
              <div className="w-12 h-12 bg-gray-200 rounded"></div>
              <div>
                <p className="text-sm">{item.name}</p>
                <p className="text-red-500 font-bold">Rs {item.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Extra Sections */}
      <div className="mt-6">
        {/* Description */}
        <div className="border p-4 rounded-lg">
          <h3 className="text-lg font-semibold">Description</h3>
          <p className="text-gray-500">No description available.</p>
        </div>

        {/* Reviews & Ratings */}
        <div className="border p-4 rounded-lg mt-4">
          <h3 className="text-lg font-semibold">Reviews & Ratings</h3>
          <div className="p-3 border rounded bg-gray-100">
            <p className="text-gray-500">No reviews yet.</p>
            <button className="bg-green-500 text-white px-3 py-2 rounded mt-2">
              Rate this Product
            </button>
          </div>
        </div>

        {/* FAQs / Product Queries */}
        <div className="border p-4 rounded-lg mt-4">
          <h3 className="text-lg font-semibold">Product Queries</h3>
          <p className="text-gray-500">Login or Register to ask a question.</p>
        </div>
      </div>
    </div>
  );
}
