import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth <= 768);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
      if (window.innerWidth > 768) {
        closeMenu();
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <header className="header">
      {/* Mobile Header */}
      {isSmallScreen && (
        <div className="mobile-header">
          <button className="menu-button" onClick={toggleMenu}>
            <i className="fa fa-bars"></i>
          </button>
          <Link to="/" className="logo">
            <i className="fa fa-store"></i> TPOP
          </Link>
        </div>
      )}

      {/* Desktop Header */}
      {!isSmallScreen && (
        <div className="desktop-header">
          <Link to="/" className="logo">
            <i className="fa fa-store"></i> TPOP
          </Link>

          <div className="search-container">
            <input
              type="text"
              placeholder="Search for products..."
              className="search-box"
            />
            <button className="search-button">
              <i className="fa fa-search"></i>
            </button>
          </div>

          <div className="profile-container">
            <i className="fa fa-user profile-icon"></i>
            <a href="/login">Login</a> | <a href="/signup">Signup</a>
            <div className="cart-container">
              <i className="fa fa-shopping-cart"></i>
              <span>Rs0.00 (0 Items)</span>
            </div>
          </div>
        </div>
      )}

      {/* Mobile Slider Menu */}
      {isSmallScreen && (
        <div className={`slider-menu ${isMenuOpen ? 'open' : ''}`}>
          <div className="menu-header">
            <button className="close-button" onClick={closeMenu}>
              <i className="fa fa-times"></i>
            </button>
            <div className="mobile-search-container">
              <input
                type="text"
                placeholder="Search..."
                className="search-box"
              />
              <button className="search-button">
                <i className="fa fa-search"></i>
              </button>
            </div>
          </div>

          <nav className="mobile-nav">
            <ul>
              <li><a href="/login" onClick={closeMenu}>Login</a></li>
              <li><a href="/signup" onClick={closeMenu}>Signup</a></li>
              <li><a href="/cart" onClick={closeMenu}>Cart</a></li>
              <li><a href="/wishlist" onClick={closeMenu}>Wishlist</a></li>
              <li><a href="/account" onClick={closeMenu}>Account</a></li>

              <li className="category-header">Categories</li>
              <li><a href="/" onClick={closeMenu}>Home Appliances</a></li>
              <li><a href="/" onClick={closeMenu}>Fashion & Clothing</a></li>
              <li><a href="/" onClick={closeMenu}>Kitchen Appliances</a></li>
              <li><a href="/" onClick={closeMenu}>Cosmetic & Beauty</a></li>
              <li><a href="/" onClick={closeMenu}>Furniture & Sofas</a></li>
              <li><a href="/" onClick={closeMenu}>Women's Fashion</a></li>
              <li><a href="/" onClick={closeMenu}>Men's Fashion</a></li>
              <li><a href="/" onClick={closeMenu}>Computer & Laptop</a></li>
              <li><a href="/" onClick={closeMenu}>Flowers & Gift Items</a></li>
              <li><a href="/" onClick={closeMenu}>Mobiles & Accessories</a></li>
            </ul>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
