import React, { useState, useRef, useEffect } from 'react';
import { Heart, ShoppingCart, ChevronLeft, ChevronRight } from 'lucide-react';
import './ProductSection.css';

function ProductSection({ title, products }) {
  const [wishlist, setWishlist] = useState([]);
  const productListRef = useRef(null);
  const [visibleProducts, setVisibleProducts] = useState(5);

  useEffect(() => {
    const updateVisibleProducts = () => {
      if (window.innerWidth <= 768) {
        setVisibleProducts(2);
      } else {
        setVisibleProducts(5);
      }
    };

    updateVisibleProducts();
    window.addEventListener('resize', updateVisibleProducts);

    return () => {
      window.removeEventListener('resize', updateVisibleProducts);
    };
  }, []);

  const toggleWishlist = (id) => {
    setWishlist((prev) =>
      prev.includes(id) ? prev.filter((item) => item !== id) : [...prev, id]
    );
  };

  const scrollLeft = () => {
    productListRef.current.scrollBy({ left: -300, behavior: 'smooth' });
  };

  const scrollRight = () => {
    productListRef.current.scrollBy({ left: 300, behavior: 'smooth' });
  };

  return (
    <div className="product-section">
      <div className="section-header">
        <h2>{title}</h2>
        <div className="nav-buttons">
          <button onClick={scrollLeft}><ChevronLeft /></button>
          <button onClick={scrollRight}><ChevronRight /></button>
        </div>
      </div>
      <div className="product-list" ref={productListRef}>
        {products.slice(0, visibleProducts).map((product) => (
          <div key={product.id} className="product-item">
            <div className="product-image">
              <span>Image</span>
            </div>
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">Rs{product.price.toFixed(2)}</p>
            <div className="product-actions">
              <button
                onClick={() => toggleWishlist(product.id)}
                className={`wishlist-button ${
                  wishlist.includes(product.id) ? 'active' : ''
                }`}
              >
                <Heart />
              </button>
              <button className="add-to-cart-button">
                Add to cart <ShoppingCart size={16} />
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductSection;
