import React, { useState, useEffect } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import MainContent from './MainContent';
import Footer from './Footer';
import './EcommerceLayout.css';

function EcommerceLayout() {
  const [categories] = useState([
    "Home Appliances",
    "Fashion & Clothing",
    "Kitchen Appliances",
    "Cosmetic & Beauty",
    "Furniture & Sofas",
    "Women's Fashion",
    "Men's Fashion",
    "Computer & Laptop",
    "Flowers & Gift Items",
    "Mobiles & Accessories",
  ]);

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [
    'https://via.placeholder.com/1100x455?text=Image+1',
    'https://via.placeholder.com/1100x455?text=Image+2',
    'https://via.placeholder.com/1100x455?text=Image+3',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="w-full min-h-screen bg-gray-100 flex flex-col">
      <Header />
      <div className="flex flex-1">
        <Sidebar categories={categories} />
        <MainContent categories={categories} />
      </div>
      <div className="relative w-full h-96 overflow-hidden my-5 bg-gray-300">
        <button className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full" onClick={prevImage}>❮</button>
        <div className="flex w-full h-full">
          {images.map((image, index) => (
            <div key={index} className={`w-full h-full flex-shrink-0 transition-transform duration-500 ${index === currentImageIndex ? 'block' : 'hidden'}`}>
              <div className="w-full h-full bg-cover bg-center" style={{ backgroundImage: `url(${image})` }}></div>
            </div>
          ))}
        </div>
        <button className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full" onClick={nextImage}>❯</button>
      </div>
      <Footer />
    </div>
  );
}

export default EcommerceLayout;
