import React, { useState, useRef, useEffect } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import MainContent from './MainContent';
import Footer from './Footer';
import { Heart, ShoppingCart, ChevronLeft, ChevronRight } from 'lucide-react';
import './EcommerceLayout.css';

function EcommerceLayout() {
  const [categories] = useState([
    "Home Appliances",
    "Fashion & Clothing",
    "Kitchen Appliances",
    "Cosmetic & Beauty",
    "Furniture & Sofas",
    "Women's Fashion",
    "Men's Fashion",
    "Computer & Laptop",
    "Flowers & Gift Items",
    "Mobiles & Accessories",
  ]);

  const featuredProducts = [
    { id: 1, name: "Product 1", price: 100 },
    { id: 2, name: "Product 2", price: 200 },
    { id: 3, name: "Product 3", price: 300 },
    { id: 4, name: "Product 4", price: 400 },
    { id: 5, name: "Product 5", price: 500 },
    { id: 6, name: "Product 6", price: 600 },
    { id: 7, name: "Product 7", price: 700 },
    { id: 8, name: "Product 8", price: 800 },
  ];

  const bestSellingProducts = [
    { id: 1, name: "Product A", price: 150 },
    { id: 2, name: "Product B", price: 250 },
    { id: 3, name: "Product C", price: 350 },
    { id: 4, name: "Product D", price: 450 },
    { id: 5, name: "Product E", price: 550 },
    { id: 6, name: "Product F", price: 650 },
    { id: 7, name: "Product G", price: 750 },
    { id: 8, name: "Product H", price: 850 },
  ];

  const topSellingProducts = [
    { id: 1, name: "Product X", price: 120 },
    { id: 2, name: "Product Y", price: 220 },
    { id: 3, name: "Product Z", price: 320 },
    { id: 4, name: "Product W", price: 420 },
    { id: 5, name: "Product V", price: 520 },
    { id: 6, name: "Product U", price: 620 },
    { id: 7, name: "Product T", price: 720 },
    { id: 8, name: "Product S", price: 820 },
  ];

  const ProductSection = ({ title, products }) => {
    const [wishlist, setWishlist] = useState([]);
    const [startIndex, setStartIndex] = useState(0);
    const [visibleProducts, setVisibleProducts] = useState(6);

    useEffect(() => {
      const updateVisibleProducts = () => {
        if (window.innerWidth <= 768) {
          setVisibleProducts(2);
        } else {
          setVisibleProducts(5);
        }
      };

      updateVisibleProducts();
      window.addEventListener('resize', updateVisibleProducts);

      return () => {
        window.removeEventListener('resize', updateVisibleProducts);
      };
    }, []);

    const toggleWishlist = (id) => {
      setWishlist((prev) =>
        prev.includes(id) ? prev.filter((item) => item !== id) : [...prev, id]
      );
    };

    const showPreviousProducts = () => {
      setStartIndex((prevIndex) => Math.max(prevIndex - visibleProducts, 0));
    };

    const showNextProducts = () => {
      setStartIndex((prevIndex) => Math.min(prevIndex + visibleProducts, products.length - visibleProducts));
    };

    return (
      <div className="product-section">
        <div className="section-header">
          <h2>{title}</h2>
          <div className="nav-buttons">
            <button onClick={showPreviousProducts} disabled={startIndex === 0}><ChevronLeft /></button>
            <button onClick={showNextProducts} disabled={startIndex + visibleProducts >= products.length}><ChevronRight /></button>
          </div>
        </div>
        <div className="product-list">
          {products.slice(startIndex, startIndex + visibleProducts).map((product) => (
            <div key={product.id} className="product-item">
              <div className="product-image">
                <span>Image</span>
              </div>
              <h3 className="product-name">{product.name}</h3>
              <p className="product-price">Rs{product.price.toFixed(2)}</p>
              <div className="product-actions">
                <button
                  onClick={() => toggleWishlist(product.id)}
                  className={`wishlist-button ${
                    wishlist.includes(product.id) ? 'active' : ''
                  }`}
                >
                  <Heart />
                </button>
                <button className="add-to-cart-button">
                  Add to cart <ShoppingCart size={16} />
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="ecommerce-layout">
      <Header />
      <div className="content">
        <Sidebar categories={categories} />
        <MainContent categories={categories} />
      </div>
      <div className="promo-image-container">
        <img src="path/to/your/promo-image.jpg" alt="Promo" className="promo-image" />
      </div>
      <ProductSection title="Featured Products" products={featuredProducts} />
      <ProductSection title="Best Selling" products={bestSellingProducts} />
      <ProductSection title="Top Selling" products={topSellingProducts} />
      <Footer />
    </div>
  );
}

export default EcommerceLayout;
